INSERT INTO portfolio (pseudo, type, presentation, style, active, photo) VALUES ('Cosmic Billie', 'team', 'Marianne alias COSMIC BILLIE, passionnée d''art et de culture indienne. Initiée très jeune au milieu du tatouage, elle a su tester plusieurs styles avant d''approfondir sa personnalité artistique. A l''écoute, souriante et soignée, elle sera ravie de vous faire découvrir son univers floral et esthétique.', 'Hindi et Ornemental', '1', 'src/img/tatoueurs/cosmic.jpg');
INSERT INTO portfolio (pseudo, type, presentation, style, active, photo) VALUES ('Gladystomeetyou', 'team', 'Agée de 22 ans, Gladys a su nourrir son univers graphique depuis sa tendre enfance. Son cursus dans le monde de l''art lui a permis de s''ouvrir à différents modes d''expression, de collaborer à des projets divers et d''exposer à plusieurs reprises. Précoce dans le milieu du tatouage, elle a su proposer son univers naïf aux lignes épurées.', 'Graphique, Naïf, épuré...', '1', 'src/img/tatoueurs/gladys.jpg');
INSERT INTO portfolio (pseudo, type, presentation, style, active, photo) VALUES ('Alex 290', 'team', 'Tombé dans le tatouage grâce à la musique, j''ai remonté le fil des références jusqu''aux sources du traditionnel occidental, qui est aujourd''hui mon domaine de prédilection.', 'Traditionnel Occidental', '1', 'src/img/tatoueurs/alex290.jpg');
